       void setToFalse(Pantalla pantalla) {
        pantalla.King.setVisible(false);
        pantalla.Queen.setVisible(false);
        pantalla.Jack.setVisible(false);
        pantalla.Ten.setVisible(false);
        pantalla.Ace.setVisible(false);
    }

    void rollDice(Pantalla pantalla) {
        new Thread() {
            @Override
            public void run() {
                try {
                    int veces = 8;
                    
                    for (int i = 0; i <= veces; i++) {

                        int random = (int) (Math.random() * 5) + 1;
                        
                        switch (random) {
                            case 1:
                                pantalla.King.setVisible(true);
                                break;
                            case 2:
                                pantalla.Queen.setVisible(true);
                                break;
                            case 3:
                                pantalla.Jack.setVisible(true);
                                break;
                            case 4:
                                pantalla.Ten.setVisible(true);
                                break;
                            case 5:
                                pantalla.Ace.setVisible(true);
                                break;
                        }

                        Thread.sleep(300);
                        if (i==veces) {
                        
                        }    
                        else {
                        pantalla.setToFalse(pantalla);}
                    }
                } catch (InterruptedException e) {
                }
            }
        }.start();

    }
    
    
